pragma solidity ^0.4.24;


import "./SafeMath.sol";
import "./LoligoToken.sol";
import "./Pausable.sol";
import "./PullPayment.sol";
import "./Whitelist.sol";
import "./TokenBonus.sol";


/*
*  Crowdsale Smart Contract for the Loligo project
*  Author: Yosra Helal yosra.helal@bechainsc.com
*/


contract Crowdsale is Pausable, PullPayment, Whitelist, TokenBonus {
    using SafeMath for uint256;


    address private wallet = 0xA167274d791bF4f9C0267300c85a92D4A1d06F8c;
    address private team = 0x7B690BB1852B7B3c592Ce9b457e09b82060Ee051;
    address private advisors = 0x7CC2b50a1234d5418D69A5022647e9D53A38564A;
    address private bounty = 0xcF6797210F1283549a5042b68AB67ae21B55e4f0;

    LoligoToken public token;

    uint256 public rate;                                                 // Rate LLG token per ether
    uint256 public presaleRate;                                          // Rate presale LLG token per ether
    // to define en ether
    uint256 public  maxFundingGoal = 43000 ether;                             // Maximum goal in Ether raised
    uint256 public  minFundingGoal = 3400 ether;                              // Minimum funding goal in Ether raised
    // dates a modifier
    uint256 public constant STARTDATEPRESALE = 1539208800;                             // epoch timestamp representing the start date (11 october 2018 00:00)
    uint256 public constant STARTDATE = 1539986400;                                    // epoch timestamp representing the start date (20 october 2018 00:00)
    uint256 public constant DEADLINE = 1543100400;                                     // epoch timestamp representing the end date (25 november 2018 00:00)
    uint256 public constant REFUNDEADLINE = 1545692400;                                // epoch timestamp representing the end date of refund period (25 december 2018 00:00)

    uint256 public totalTokensForSale = 11200000000000000000000000;                   // 11 200 000 LLG tokens will be sold in Crowdsale
    uint256 public totalTokensForPresale;                                             // LLG tokens will be sold in Presale
    uint256 public tokensForTeam = 480000000000000000000000;
    uint256 public tokensForBounty = 480000000000000000000000;
    uint256 public tokensForAdvisors = 1440000000000000000000000;

    uint256 public savedBalance = 0;                                                   // Total amount raised in ETH
    uint256 public savedTokenBalance = 0;                                              // Total sold tokens for presale
    uint256 public savedPresaleTokenBalance = 0;                                       // Total sold tokens for crowdsale

    bool public crowdsale;                                                     // crowdsale period
    bool public presale;                                                        // presale period

    mapping (address => uint256) balances;                                             // Balances in incoming Ether

    // Events to record new contributions
    event Contribution(address indexed _contributor, uint256 indexed _value, uint256 indexed _tokens);

    // Event to record each time Ether is paid out
    event PayEther(
    address indexed _receiver,
    uint256 indexed _value,
    uint256 indexed _timestamp
    );

    // Event to record when tokens are burned.
    event BurnTokens(
    uint256 indexed _value,
    uint256 indexed _timestamp
    );

    // Initialization
    constructor(address _token) public {
        // add address of the specific contract
        token = LoligoToken(_token);
    }


    // Default Function, delegates to contribute function (for ease of use)
    // WARNING: Not compatible with smart contract invocation, will exceed gas stipend!
    // Only use from external accounts
    function () public payable whenNotPaused {
        if (msg.sender == wallet) {
            balances[this] = msg.value;
        }else {
            if (presale && now >= STARTDATEPRESALE) {
                buyPresaleTokens(msg.sender);
            }else {
                if(crowdsale && now >= STARTDATE) {
                  buyTokens(msg.sender);
                }
            }
        }
    }

    function closePresale() public onlyOwner {
        presale = false;
    }

    // Function to set Rate & tokens to sell for presale
    function setParamsPresale(uint256 _rate, uint256 _totalTokensForPresale) public onlyOwner {
        presaleRate = _rate;
        totalTokensForPresale = _totalTokensForPresale;
        presale = true;
    }

    function setParams(uint256 _rate, uint256 _maxCap, uint256 _minCap) public onlyOwner {
        require(!presale);
        rate = _rate;
        maxFundingGoal = _maxCap;
        minFundingGoal = _minCap;
        crowdsale = true;
    }

    // Function to check if crowdsale is complete (
    function isComplete() public view returns (bool) {
        return (savedBalance >= maxFundingGoal) || (now > DEADLINE) ||
        (savedTokenBalance >= totalTokensForSale) || (crowdsale == false);
    }

    // Function to check if crowdsale has been successful
    // (has incoming contribution balance met, or exceeded the minimum goal?)
    function isSuccessful() public view returns (bool) {
        return (savedBalance >= minFundingGoal);
    }

    // Function to check the refund period is over
    function refundPeriodOver() public view returns (bool) {
        return (now > REFUNDEADLINE);
    }

    // Function to check the refund period is over
    function refundPeriodStart() public view returns (bool) {
        return (now > DEADLINE);
    }

    // Only owner will finalize the crowdsale
    function finalize(address _newOwner) public onlyOwner {
        require(!presale);
        crowdsale = false;
        transferTokenOwnership(_newOwner);
    }

    // Function to pay out
    function payout(address _newOwner) public onlyOwner {
        if (isSuccessful() && isComplete()) {
            require(token.transfer(team, tokensForTeam));
            require(token.transfer(bounty, tokensForBounty));
            require(token.transfer(advisors, tokensForAdvisors));
            uint256 tokensToBurn =  token.balanceOf(address(this)).sub(savedBonusToken);
            require(token.burn(tokensToBurn));
            transferTokenOwnership(_newOwner);
            crowdsale = false;
        }else {
            if (refundPeriodOver()) {
                wallet.transfer(address(this).balance);
                emit PayEther(wallet, address(this).balance, now);
                require(token.burn(token.balanceOf(address(this))));
                transferTokenOwnership(_newOwner);
            }
        }
    }

    /* When MIN_CAP is not reach the smart contract will be credited to make refund possible by backers
     * 1) backer call the "refund" function of the Crowdsale contract
     * 2) backer call the "withdrawPayments" function of the Crowdsale contract to get a refund in ETH
     */
    function refund() public {
        require(!isSuccessful());
        require(refundPeriodStart());
        require(!refundPeriodOver());
        require(balances[msg.sender] > 0);
        uint amountToRefund = (balances[msg.sender].mul(95)).div(100);
        balances[msg.sender] = 0;
        asyncSend(msg.sender, amountToRefund);
        savedBalance = savedBalance.sub(amountToRefund);
    }

    function transferTokenOwnership(address _newOwner) public onlyOwner {
        token.transferOwnership(_newOwner);
    }

    // Contribute Function, accepts incoming payments and tracks balances for each contributors
    function buyTokens(address _beneficiary) internal {
        require(!isComplete());
        require((savedBalance.add(msg.value)) <= maxFundingGoal);
        require((savedTokenBalance.add(msg.value.div(rate))) <= totalTokensForSale);

        if (isWhitelisted(_beneficiary)) {
            uint256 tokensAmount;
            require(msg.value >= 1 ether);
            if (msg.value >= 10 ether) {
                savedBalance = savedBalance.add(msg.value);
                tokensAmount = msg.value.div(presaleRate);
                uint256 bonus = tokensAmount.mul(30).div(100);
                savedTokenBalance = savedTokenBalance.add(tokensAmount.add(bonus));
                token.transfer(_beneficiary, tokensAmount);
                savedBonusToken = savedBonusToken.add(bonus);
                bonusBalances[_beneficiary] = bonusBalances[_beneficiary].add(bonus);
                bonusList.push(_beneficiary);
                wallet.transfer(msg.value);
                emit PayEther(wallet, msg.value, now);
            }else {
                savedBalance = savedBalance.add(msg.value);
                tokensAmount = msg.value.div(presaleRate);
                uint256 tokensToTransfer = tokensAmount.add((tokensAmount.mul(30)).div(100));
                savedTokenBalance = savedTokenBalance.add(tokensToTransfer);
                token.transfer(_beneficiary, tokensToTransfer);
                wallet.transfer(msg.value);
                emit PayEther(wallet, msg.value, now);
            }
        }else {
            balances[_beneficiary] = balances[_beneficiary].add(msg.value);
            savedBalance = savedBalance.add(msg.value);
            savedTokenBalance = savedTokenBalance.add(msg.value.div(rate));
            token.transfer(_beneficiary, msg.value.div(rate));
            wallet.transfer(msg.value);
            emit PayEther(wallet, msg.value, now);
        }
    }

    function buyPresaleTokens(address _beneficiary) internal {
        require(msg.value >= 1 ether);
        require(isPresaleWhitelisted(_beneficiary));
        require((savedBalance.add(msg.value)) <= maxFundingGoal);
        require((savedPresaleTokenBalance.add(msg.value.div(presaleRate))) <= totalTokensForPresale);
        uint256 tokensAmount;

        if (msg.value >= 10 ether) {
            savedBalance = savedBalance.add(msg.value);
            tokensAmount = msg.value.div(presaleRate);
            uint256 bonus = tokensAmount.mul(30).div(100);
            savedTokenBalance = savedTokenBalance.add(tokensAmount.add(bonus));
            token.transfer(_beneficiary, tokensAmount);
            savedBonusToken = savedBonusToken.add(bonus);
            bonusBalances[_beneficiary] = bonusBalances[_beneficiary].add(bonus);
            bonusList.push(_beneficiary);
            wallet.transfer(msg.value);
            emit PayEther(wallet, msg.value, now);
        }else {
            savedBalance = savedBalance.add(msg.value);
            tokensAmount = msg.value.div(presaleRate);
            uint256 tokensToTransfer = tokensAmount.add((tokensAmount.mul(30)).div(100));
            savedTokenBalance = savedTokenBalance.add(tokensToTransfer);
            token.transfer(_beneficiary, tokensToTransfer);
            wallet.transfer(msg.value);
            emit PayEther(wallet, msg.value, now);
        }
    }
}
